\n// Prepare data for POST request\n        $fields = array(\n            \'apiKey\'=> \'YOUR API KEY\',\n            \'sender\' => \'mycompany\',\n            \'scheduledDeliveryDate\'=> \'21/10/2014\',\n            \'time\'=> 9,\n            \'minute\'=> 0,\n            \'SMSList\'=> array(\n                array(\n                    \'phoneNumber\'=> \'06xxxxxxx1\',\n                    \'message\'=> \'msg 0\'\n                ),\n                array(\n                    \'phoneNumber\'=> \'06xxxxxxx2\',\n                    \'message\'=> \'msg 1\'\n                )\n \n            )\n        );\n \n \n        $curl = curl_init();\n        curl_setopt($curl, CURLOPT_URL,\'https://api.smspartner.fr/v1/bulk-send\');\n        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\n        curl_setopt($curl, CURLOPT_TIMEOUT, 10);\n        curl_setopt($curl, CURLOPT_POST, 1);\n        curl_setopt($curl, CURLOPT_POSTFIELDS,json_encode($fields));\n \n        $result = curl_exec($curl);\n        curl_close($curl);\n \n        // Process your response here\n        echo $result;\n?&gt;\n    </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"vbnet\" role=\"tabpanel\" aria-labelledby=\"vbnet-tab\">\n   <pre><code class=\"language-vbnet\">\nImports System.IO\nImports System.Net\n \nModule Module1\n \n  Sub Main()\n \n    Dim base_url As String = \"http://api.smspartner.fr/v1/\"\n    Dim apiKey As String = \"VOTRE_APIKEY\"\n \n    #send sms\n    url = base_url & \"bulk-send\"\n    #note : utiliser une librairie JSON en production, par exemple :\n    #https//www.nuget.org/packages/Newtonsoft.Json/\n    Dim parameters As String = String.Format(\n        \"{{\"\"apiKey\"\":\"\"{0}\"\",\"\"sender\"\":\"\"{1}\"\",\"\"SMSList\"\":\"\"{2}\"\"}}\",\n        apiKey,\n        \"mycompany\",\n        {{ \"\"phoneNumber\"\":\"06xxxxxxx1\",\"\"message\"\":\"msg1\"},{ \"\"phoneNumber\"\":\"06xxxxxxx2\",\"\"message\"\":\"msg2\"}}\n        )\n    Console.Write(parameters)\n    apiRequest(\"POST\", url, parameters)\n \n  End Sub\n \n  Function apiRequest(method As String, url As String, parameters As String) As String\n \n    Dim request As HttpWebRequest\n    request = WebRequest.Create(url)\n    request.Method = method\n    request.Timeout = 10000   # timeout in ms\n    request.ContentType = \"application/json; charset=utf-8\"\n    request.ContentLength = 0\n \n    #set POST data\n    If Not String.IsNullOrEmpty(parameters) Then\n      request.ContentLength = parameters.Length\n      Using reqStream As StreamWriter = New StreamWriter(request.GetRequestStream())\n        reqStream.Write(parameters)\n      End Using\n    End If\n \n    #get response\n    Dim returnValue As String = Nothing\n    Using response As HttpWebResponse = request.GetResponse()\n      If response.StatusCode = HttpStatusCode.OK Then\n        Using resStream = response.GetResponseStream()\n          If resStream IsNot Nothing Then\n            Using reader As New StreamReader(resStream)\n              returnValue = reader.ReadToEnd()\n            End Using\n          End If\n        End Using\n      End If\n    End Using\n    apiRequest = returnValue\n \n  End Function\n \nEnd Module\n\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"python\" role=\"tabpanel\" aria-labelledby=\"python-tab\">\n    <!-- Python code example goes here -->\n    <pre><code class=\"language-python\">\n# std\nimport logging\nimport json\nfrom collections import OrderedDict\n \n# 3p\nimport requests\n \nAPI_KEY = \"MY API KEY\"\nURL = \"https://api.smspartner.fr/v1\"\n \nclass SMSPartner():\n    def send_sms(self,phone_numbers, msg, sender = \"SMSPartner\"):\n		#sender = \"DEMOSMS\"\n		print(phone_numbers)\n \n \n		data = {\"apiKey\":APIKEY,\"sender\":sender,\"SMSList\":[{\"phoneNumber\":\"06xxxxxxx1\",\"message\":\"msg1\"},{\"phoneNumber\":\"06xxxxxxx2\",\"message\":\"msg2\"}]}\n \n \n		url = URL + \"/bulk-send\"\n		r = requests.post(url, data=json.dumps(data), verify=False)\n \n		r_json = r.json()\n		if r_json.get(\"success\") == True:\n			print(r_json)\n			status = True\n		else:\n			print(\"SMS msg {} not delivered to {}\".format(msg, phone_numbers))\n			status = False\n		return status\n   </code></pre>\n  </div>\n \n    <div class=\"tab-pane fade\" id=\"curl\" role=\"tabpanel\" aria-labelledby=\"curl-tab\">\n    <!-- cURL code example goes here -->\n    <pre><code class=\"language-bash\">\n    curl -H  \"Content-Type: application/json\" -X POST -d \'{\"apiKey\":\"xxxxx\",\"to\":\"xxxx\",\"from\":\"xxx\",\"message\":\"test\"}\' https://api.smspartner.fr/v1/vn/send\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"nodejs\" role=\"tabpanel\" aria-labelledby=\"nodejs-tab\">\n    <!-- NodeJS code example goes here -->\n    <pre><code class=\"language-javascript\">\nconst https = require(\'https\');\n\n// Remplacez par votre cl√© API\nconst apiKey = \'VOTRE_CL√â_API\';\n\n// Objet JSON qui contient les informations n√©cessaires pour envoyer les SMS en lots\nconst data = JSON.stringify({\n    apiKey: apiKey,\n    sender: \'mycompany\',\n    scheduledDeliveryDate: \'21/10/2014\',\n    time: 9,\n    minute: 0,\n    SMSList: [\n        {\n            phoneNumber: \'06xxxxxxx1\',\n            message: \'msg 0\',\n        },\n        {\n            phoneNumber: \'06xxxxxxx2\',\n            message: \'msg 1\',\n        },\n    ],\n});\n\n// D√©finir les options pour la requ√™te HTTP POST vers l\'API SMS Partner\nconst options = {\n  hostname: \'api.smspartner.fr\',\n  port: 443,\n  path: \'/v1/send\',\n  method: \'POST\',\n  headers: {\n    \'Content-Type\': \'application/json\',\n    \'Content-Length\': data.length,\n    \'cache-control\': \'no-cache\',\n  },\n};\n\n// Demande HTTP POST avec les options et les donn√©es d√©finies pr√©c√©demment\nconst req = https.request(options, (res) => {\n  console.log(`statusCode: ${res.statusCode}`);\n  \n  // Afficher les donn√©es de r√©ponse de l\'API sur la sortie standard\n  res.on(\'data\', (d) => {\n    process.stdout.write(d);\n  });\n});\n\n// Affichage en cas d\'erreur lors de l\'ex√©cution de la requ√™te HTTP POST\nreq.on(\'error\', (error) => {\n  console.error(error);\n});\n\n// Envoyer les donn√©es de l\'objet \'data\' √† la demande\nreq.write(data);\n// Terminer la demande HTTP POST\nreq.end();\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"java\" role=\"tabpanel\" aria-labelledby=\"java-tab\">\n    <!-- JAVA code example goes here -->\n   <pre><code class=\"language-java\">\nimport java.net.HttpURLConnection;\nimport java.net.URL;\nimport java.io.OutputStream;\nimport java.io.BufferedReader;\nimport java.io.InputStreamReader;\nimport java.util.stream.Collectors;\nimport org.json.JSONArray;\nimport org.json.JSONObject;\n\npublic class SMSRequest {\n    public static void main(String[] args) {\n        try {\n        // Cr√©ation de l\'objet URL avec l\'adresse de l\'API SMS\n               URL url = new URL(\"https://api.smspartner.fr/v1/bulk-send\"\n        // Ouverture de la connexion HTTP avec l\'API\n            HttpURLConnection conn = (HttpURLConnection) url.openConnection();\n            conn.setRequestMethod(\"POST\");\n            conn.setRequestProperty(\"Content-Type\", \"application/json\");\n            conn.setRequestProperty(\"cache-control\", \"no-cache\");\n            conn.setDoOutput(true);\n\n            // Cr√©ation de l\'objet JSON contenant les param√®tres des SMS √† envoyer en lot\n            JSONObject json = new JSONObject();\n            json.put(\"apiKey\", \"VOTRE_API_KEY\"); // Remplacez par votre cl√© API\n            json.put(\"sender\", \"mycompany\");\n            json.put(\"scheduledDeliveryDate\", \"21/10/2014\");\n            json.put(\"time\", 9);\n            json.put(\"minute\", 0);\n\n            JSONArray SMSList = new JSONArray();\n            JSONObject sms1 = new JSONObject();\n            sms1.put(\"phoneNumber\", \"06xxxxxxx1\");\n            sms1.put(\"message\", \"msg 0\");\n            SMSList.put(sms1);\n\n            JSONObject sms2 = new JSONObject();\n            sms2.put(\"phoneNumber\", \"06xxxxxxx2\");\n            sms2.put(\"message\", \"msg 1\");\n            SMSList.put(sms2);\n\n            json.put(\"SMSList\", SMSList);\n\n        \n        // √âcriture des donn√©es JSON dans le corps de la requ√™te HTTP\n            OutputStream os = conn.getOutputStream(); \n            os.write(json.toString().getBytes());\n            os.flush();\n\n        // Lecture de la r√©ponse de l\'API\n            BufferedReader br = new BufferedReader(new InputStreamReader(conn.getInputStream()));\n            String response = br.lines().collect(Collectors.joining());\n            System.out.println(response);\n            \n        // Fermeture de la connexion HTTP\n            conn.disconnect();\n        } catch (Exception e) {\n            e.printStackTrace();\n        }\n    }\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"swift\" role=\"tabpanel\" aria-labelledby=\"swift-tab\">\n    <!-- SWIFT code example goes here -->\n    <pre><code class=\"language-swift\">\nimport SwiftUI\n\n// Cette requ√™te est utilis√©e pour envoyer des SMS par lot de 500 en temps r√©el ou en diff√©r√©\nstruct SMSParLotsView: View {\n    var body: some View {\n        // Un bouton qui appelle la fonction sendSMSParLots() lorsqu\'il est press√©\n        Button(action: {\n            sendSMSParLots()\n        }) {\n            Text(\"Send Bulk SMS\")\n                .font(.system(size: 20))\n                .foregroundColor(.white)\n                .frame(minWidth: 0, maxWidth: .infinity)\n                .padding()\n                .background(LinearGradient(gradient: Gradient(colors: [Color.blue, Color.blue.opacity(0.8)]), startPoint: .top, endPoint: .bottom))\n                .cornerRadius(10)\n                .padding(.horizontal)\n        }\n    }\n    \n\n    func sendSMSParLots() {\n        // Liste des SMS √† envoyer\n        let smsList: [[String: String]] = [\n            [\n                \"phoneNumber\": \"06XXXXXXXX\", // Num√©ro de t√©l√©phone du destinataire\n                \"message\": \"msg 0 en temps r√©el\" // Message √† envoyer\n            ],\n            [\n                \"phoneNumber\": \"06XXXXXXXX\", // Un autre num√©ro de t√©l√©phone\n                \"message\": \"msg 1 en temps r√©el\" // Un autre message\n            ]\n        ]\n\n        // Donn√©es √† envoyer √† l\'API\n        let data: [String: Any] = [\n            \"apiKey\": \"XXXXXXXXXXXX YOUR API KEY XXXXXXXXXXXXX\", // Votre cl√© API\n            \"sender\": \"mycompany\", // L\'exp√©diteur du SMS\n            // Ces 3 lignes permettent de programmer un envoi diff√©r√©.\n            //\"scheduledDeliveryDate\": \"12/05/2023\", // Date de livraison pr√©vue\n            //\"time\": 9, // Heure de livraison\n            //\"minute\": 0, // Minute de livraison\n            \"SMSList\": smsList // La liste des SMS √† envoyer\n        ]\n\n        // Conversion des donn√©es en JSON\n        guard let jsonData = try? JSONSerialization.data(withJSONObject: data, options: []) else {\n            fatalError(\"Error converting data to JSON.\")\n        }\n\n        // Cr√©ation de la requ√™te HTTP\n        let url = URL(string: \"https://api.smspartner.fr/v1/bulk-send\")! // URL de l\'API\n        var request = URLRequest(url: url)\n        request.httpMethod = \"POST\" // M√©thode HTTP\n        request.httpBody = jsonData // Corps de la requ√™te\n        request.addValue(\"application/json\", forHTTPHeaderField: \"Content-Type\") // Type de contenu\n        request.addValue(\"(jsonData.count)\", forHTTPHeaderField: \"Content-Length\") // Longueur du contenu\n        request.addValue(\"no-cache\", forHTTPHeaderField: \"cache-control\") // Contr√¥le du cache\n\n        // Envoi de la requ√™te HTTP\n        let task = URLSession.shared.dataTask(with: request) { (data, response, error) in\n            // V√©rification de la r√©ponse HTTP\n            guard let httpResponse = response as? HTTPURLResponse else {\n                fatalError(\"Error: Invalid HTTP response.\")\n            }\n            print(\"statusCode: (httpResponse.statusCode)\") // Impression du code d\'√©tat HTTP\n\n            // Gestion des erreurs √©ventuelles\n            if let error = error {\n                print(\"Error: (error.localizedDescription)\")\n            }\n\n            // Impression de la r√©ponse de l\'API\n            if let data = data {\n                if let responseString = String(data: data, encoding: .utf8) {\n                    print(\"Response: (responseString)\")\n                }\n            }\n        }\n        task.resume()\n    }\n}\n// Un aper√ßu de la vue\nstruct BulkSMSView_Previews: PreviewProvider {\n    static var previews: some View {\n        SMSParLotsView()\n    }\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"go\" role=\"tabpanel\" aria-labelledby=\"go-tab\">\n    <!-- GO code example goes here -->\n    <pre><code class=\"language-go\">\npackage main\n\nimport (\n	\"bytes\"\n	\"encoding/json\"\n	\"fmt\"\n	\"net/http\"\n)\n\nfunc main() {\n	apiKey := \"VOTRE CLE API\"\n	sender := \"monentreprise\"\n	scheduledDeliveryDate := \"21/10/2014\"\n	time := 9\n	minute := 0\n\n	// Construire les donn√©es JSON pour la requ√™te POST\n	data := map[string]interface{}{\n		\"apiKey\":               apiKey,\n		\"sender\":               sender,\n		\"scheduledDeliveryDate\": scheduledDeliveryDate,\n		\"time\":                 time,\n		\"minute\":               minute,\n		\"SMSList\": []map[string]string{\n			{\"phoneNumber\": \"06xxxxxxx1\", \"message\": \"msg 0\"},\n			{\"phoneNumber\": \"06xxxxxxx2\", \"message\": \"msg 1\"},\n		},\n	}\n\n	// Convertir les donn√©es en JSON\n	jsonData, err := json.Marshal(data)\n	if err != nil {\n		fmt.Println(\"Erreur lors de la conversion en JSON:\", err)\n		return\n	}\n\n	// Effectuer la requ√™te POST\n	url := \"https://api.smspartner.fr/v1/bulk-send\"\n	resp, err := http.Post(url, \"application/json\", bytes.NewBuffer(jsonData))\n	if err != nil {\n		fmt.Println(\"Erreur lors de l\'envoi de la requ√™te:\", err)\n		return\n	}\n	defer resp.Body.Close()\n\n	// Traiter la r√©ponse\n	if resp.StatusCode == http.StatusOK {\n		body := new(bytes.Buffer)\n		_, err := body.ReadFrom(resp.Body)\n		if err != nil {\n			fmt.Println(\"Erreur lors de la lecture de la r√©ponse:\", err)\n			return\n		}\n		fmt.Println(body.String())\n	} else {\n		fmt.Println(\"La requ√™te POST a √©chou√©. Code de r√©ponse:\", resp.StatusCode)\n	}\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"csharp\" role=\"tabpanel\" aria-labelledby=\"csharp-tab\">\n    <!-- C# code example goes here -->\n    <pre><code class=\"language-csharp\">\nusing System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\nusing Newtonsoft.Json;\n\nclass Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    static async Task Main(string[] args)\n    {\n        var request = new\n        {\n            apiKey = \"YOUR_API_KEY\",\n            sender = \"mycompany\",\n            scheduledDeliveryDate = \"21/10/2014\",\n            time = 9,\n            minute = 0,\n            SMSList = new[]\n            {\n                new\n                {\n                    phoneNumber = \"06xxxxxxx1\",\n                    message = \"msg 0\"\n                },\n                new\n                {\n                    phoneNumber = \"06xxxxxxx2\",\n                    message = \"msg 1\"\n                }\n            }\n        };\n\n        var content = new StringContent(\n            JsonConvert.SerializeObject(request),\n            Encoding.UTF8,\n            \"application/json\");\n\n        HttpResponseMessage response = await client.PostAsync(\"https://api.smspartner.fr/v1/bulk-send\", content);\n\n        if (response.IsSuccessStatusCode)\n        {\n            var result = await response.Content.ReadAsStringAsync();\n            Console.WriteLine(result);\n        }\n        else\n        {\n            Console.WriteLine(\"POST request failed with status code: \" + response.StatusCode);\n        }\n    }\n}\n   </code></pre>\n  </div>\n</div>\n\n## R√©ponses\n\n### JSON\n```json\n{\n       \"success\": true,\n    \"code\": 200,\n    \"message_id\": xxxxxx,\n    \"currency\": \"EUR\",\n    \"cost\": 0.076,\n    \"nbSMS\": 2,\n    \"SMSResponse_List\": [\n        {\n            \"success\": true,\n            \"code\": 200,\n            \"nbSms\": 1,\n            \"cost\": 0.038,\n            \"phoneNumber\": \"+336xxxxxxx1\"\n        },\n        {\n            \"success\": true,\n            \"code\": 200,\n            \"nbSms\": 1,\n            \"cost\": 0.038,\n            \"phoneNumber\": \"+336xxxxxxx2\"\n        }\n    ]\n}\n```\n### XML\n```xml\n    <?xml version=\'1.0\' encoding=\'UTF-8\'?>\n<result>\n    <entry>true</entry>\n    <entry>200</entry>\n    <entry>xxxxxx</entry>\n    <entry>\n        <![CDATA[EUR]]>\n    </entry>\n    <entry>0.076</entry>\n    <entry>2</entry>\n    <entry>\n        <entry>\n            <entry>true</entry>\n            <entry>200</entry>\n            <entry>1</entry>\n            <entry>0.038</entry>\n            <entry>\n                <![CDATA[+336xxxxxxx1]]>\n            </entry>\n        </entry>\n        <entry>\n            <entry>true</entry>\n            <entry>200</entry>\n            <entry>1</entry>\n            <entry>0.038</entry>\n            <entry>\n                <![CDATA[+336xxxxxxx2]]>\n            </entry>\n        </entry>\n    </entry>\n</result>\n  ```\n', 'fr', 17),
(15, '## Request\nExample of a request\n\n\n<!-- Nav tabs -->\n<ul class=\"nav nav-tabs\" id=\"myTab\" role=\"tablist\">\n  <li class=\"nav-item\">\n    <a class=\"nav-link active\" id=\"php-tab\" data-toggle=\"tab\" href=\"#php\" role=\"tab\" aria-controls=\"php\" aria-selected=\"true\">PHP</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"vbnet-tab\" data-toggle=\"tab\" href=\"#vbnet\" role=\"tab\" aria-controls=\"vbnet\" aria-selected=\"false\">VB.net</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"python-tab\" data-toggle=\"tab\" href=\"#python\" role=\"tab\" aria-controls=\"python\" aria-selected=\"false\">Python</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"curl-tab\" data-toggle=\"tab\" href=\"#curl\" role=\"tab\" aria-controls=\"curl\" aria-selected=\"false\">cURL</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"nodejs-tab\" data-toggle=\"tab\" href=\"#nodejs\" role=\"tab\" aria-controls=\"nodejs\" aria-selected=\"false\">NodeJS</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"java-tab\" data-toggle=\"tab\" href=\"#java\" role=\"tab\" aria-controls=\"java\" aria-selected=\"false\">JAVA</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"swift-tab\" data-toggle=\"tab\" href=\"#swift\" role=\"tab\" aria-controls=\"swift\" aria-selected=\"false\">SWIFT</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"go-tab\" data-toggle=\"tab\" href=\"#go\" role=\"tab\" aria-controls=\"go\" aria-selected=\"false\">GO</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"csharp-tab\" data-toggle=\"tab\" href=\"#csharp\" role=\"tab\" aria-controls=\"csharp\" aria-selected=\"false\">C#</a>\n  </li>\n</ul>\n\n<!-- Tab panes -->\n<div class=\"tab-content\">\n  <div class=\"tab-pane fade show active\" id=\"php\" role=\"tabpanel\" aria-labelledby=\"php-tab\">\n    <pre><code class=\"language-php\">\n&lt;?php\n// Prepare data for POST request\n        $fields = array(\n            \'apiKey\'=> \'YOUR API KEY\',\n            \'sender\' => \'mycompany\',\n            \'scheduledDeliveryDate\'=> \'21/10/2014\',\n            \'time\'=> 9,\n            \'minute\'=> 0,\n            \'SMSList\'=> array(\n                array(\n                    \'phoneNumber\'=> \'06xxxxxxx1\',\n                    \'message\'=> \'msg 0\'\n                ),\n                array(\n                    \'phoneNumber\'=> \'06xxxxxxx2\',\n                    \'message\'=> \'msg 1\'\n                )\n \n            )\n        );\n \n \n        $curl = curl_init();\n        curl_setopt($curl, CURLOPT_URL,\'https://api.smspartner.fr/v1/bulk-send\');\n        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\n        curl_setopt($curl, CURLOPT_TIMEOUT, 10);\n        curl_setopt($curl, CURLOPT_POST, 1);\n        curl_setopt($curl, CURLOPT_POSTFIELDS,json_encode($fields));\n \n        $result = curl_exec($curl);\n        curl_close($curl);\n \n        // Process your response here\n        echo $result;\n?&gt;\n    </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"vbnet\" role=\"tabpanel\" aria-labelledby=\"vbnet-tab\">\n   <pre><code class=\"language-vbnet\">\nImports System.IO\nImports System.Net\n \nModule Module1\n \n  Sub Main()\n \n    Dim base_url As String = \"http://api.smspartner.fr/v1/\"\n    Dim apiKey As String = \"VOTRE_APIKEY\"\n \n    #send sms\n    url = base_url & \"bulk-send\"\n    #note : utiliser une librairie JSON en production, par exemple :\n    #https//www.nuget.org/packages/Newtonsoft.Json/\n    Dim parameters As String = String.Format(\n        \"{{\"\"apiKey\"\":\"\"{0}\"\",\"\"sender\"\":\"\"{1}\"\",\"\"SMSList\"\":\"\"{2}\"\"}}\",\n        apiKey,\n        \"mycompany\",\n        {{ \"\"phoneNumber\"\":\"06xxxxxxx1\",\"\"message\"\":\"msg1\"},{ \"\"phoneNumber\"\":\"06xxxxxxx2\",\"\"message\"\":\"msg2\"}}\n        )\n    Console.Write(parameters)\n    apiRequest(\"POST\", url, parameters)\n \n  End Sub\n \n  Function apiRequest(method As String, url As String, parameters As String) As String\n \n    Dim request As HttpWebRequest\n    request = WebRequest.Create(url)\n    request.Method = method\n    request.Timeout = 10000   # timeout in ms\n    request.ContentType = \"application/json; charset=utf-8\"\n    request.ContentLength = 0\n \n    #set POST data\n    If Not String.IsNullOrEmpty(parameters) Then\n      request.ContentLength = parameters.Length\n      Using reqStream As StreamWriter = New StreamWriter(request.GetRequestStream())\n        reqStream.Write(parameters)\n      End Using\n    End If\n \n    #get response\n    Dim returnValue As String = Nothing\n    Using response As HttpWebResponse = request.GetResponse()\n      If response.StatusCode = HttpStatusCode.OK Then\n        Using resStream = response.GetResponseStream()\n          If resStream IsNot Nothing Then\n            Using reader As New StreamReader(resStream)\n              returnValue = reader.ReadToEnd()\n            End Using\n          End If\n        End Using\n      End If\n    End Using\n    apiRequest = returnValue\n \n  End Function\n \nEnd Module\n\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"python\" role=\"tabpanel\" aria-labelledby=\"python-tab\">\n    <!-- Python code example goes here -->\n    <pre><code class=\"language-python\">\n# std\nimport logging\nimport json\nfrom collections import OrderedDict\n \n# 3p\nimport requests\n \nAPI_KEY = \"MY API KEY\"\nURL = \"https://api.smspartner.fr/v1\"\n \nclass SMSPartner():\n    def send_sms(self,phone_numbers, msg, sender = \"SMSPartner\"):\n		#sender = \"DEMOSMS\"\n		print(phone_numbers)\n \n \n		data = {\"apiKey\":APIKEY,\"sender\":sender,\"SMSList\":[{\"phoneNumber\":\"06xxxxxxx1\",\"message\":\"msg1\"},{\"phoneNumber\":\"06xxxxxxx2\",\"message\":\"msg2\"}]}\n \n \n		url = URL + \"/bulk-send\"\n		r = requests.post(url, data=json.dumps(data), verify=False)\n \n		r_json = r.json()\n		if r_json.get(\"success\") == True:\n			print(r_json)\n			status = True\n		else:\n			print(\"SMS msg {} not delivered to {}\".format(msg, phone_numbers))\n			status = False\n		return status\n   </code></pre>\n  </div>\n  \n    <div class=\"tab-pane fade\" id=\"curl\" role=\"tabpanel\" aria-labelledby=\"curl-tab\">\n    <!-- cURL code example goes here -->\n    <pre><code class=\"language-bash\">\n    curl -H  \"Content-Type: application/json\" -X POST -d \'{\"apiKey\":\"xxxxx\",\"to\":\"xxxx\",\"from\":\"xxx\",\"message\":\"test\"}\' https://api.smspartner.fr/v1/vn/send\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"nodejs\" role=\"tabpanel\" aria-labelledby=\"nodejs-tab\">\n    <!-- NodeJS code example goes here -->\n    <pre><code class=\"language-javascript\">\nconst https = require(\'https\');\n\n// Remplacez par votre cl√© API\nconst apiKey = \'VOTRE_CL√â_API\';\n\n// Objet JSON qui contient les informations n√©cessaires pour envoyer les SMS en lots\nconst data = JSON.stringify({\n    apiKey: apiKey,\n    sender: \'mycompany\',\n    scheduledDeliveryDate: \'21/10/2014\',\n    time: 9,\n    minute: 0,\n    SMSList: [\n        {\n            phoneNumber: \'06xxxxxxx1\',\n            message: \'msg 0\',\n        },\n        {\n            phoneNumber: \'06xxxxxxx2\',\n            message: \'msg 1\',\n        },\n    ],\n});\n\n// D√©finir les options pour la requ√™te HTTP POST vers l\'API SMS Partner\nconst options = {\n  hostname: \'api.smspartner.fr\',\n  port: 443,\n  path: \'/v1/send\',\n  method: \'POST\',\n  headers: {\n    \'Content-Type\': \'application/json\',\n    \'Content-Length\': data.length,\n    \'cache-control\': \'no-cache\',\n  },\n};\n\n// Demande HTTP POST avec les options et les donn√©es d√©finies pr√©c√©demment\nconst req = https.request(options, (res) => {\n  console.log(`statusCode: ${res.statusCode}`);\n  \n  // Afficher les donn√©es de r√©ponse de l\'API sur la sortie standard\n  res.on(\'data\', (d) => {\n    process.stdout.write(d);\n  });\n});\n\n// Affichage en cas d\'erreur lors de l\'ex√©cution de la requ√™te HTTP POST\nreq.on(\'error\', (error) => {\n  console.error(error);\n});\n\n// Envoyer les donn√©es de l\'objet \'data\' √† la demande\nreq.write(data);\n// Terminer la demande HTTP POST\nreq.end();\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"java\" role=\"tabpanel\" aria-labelledby=\"java-tab\">\n    <!-- JAVA code example goes here -->\n   <pre><code class=\"language-java\">\nimport java.net.HttpURLConnection;\nimport java.net.URL;\nimport java.io.OutputStream;\nimport java.io.BufferedReader;\nimport java.io.InputStreamReader;\nimport java.util.stream.Collectors;\nimport org.json.JSONArray;\nimport org.json.JSONObject;\n\npublic class SMSRequest {\n    public static void main(String[] args) {\n        try {\n        // Cr√©ation de l\'objet URL avec l\'adresse de l\'API SMS\n               URL url = new URL(\"https://api.smspartner.fr/v1/bulk-send\"\n        // Ouverture de la connexion HTTP avec l\'API\n            HttpURLConnection conn = (HttpURLConnection) url.openConnection();\n            conn.setRequestMethod(\"POST\");\n            conn.setRequestProperty(\"Content-Type\", \"application/json\");\n            conn.setRequestProperty(\"cache-control\", \"no-cache\");\n            conn.setDoOutput(true);\n\n            // Cr√©ation de l\'objet JSON contenant les param√®tres des SMS √† envoyer en lot\n            JSONObject json = new JSONObject();\n            json.put(\"apiKey\", \"VOTRE_API_KEY\"); // Remplacez par votre cl√© API\n            json.put(\"sender\", \"mycompany\");\n            json.put(\"scheduledDeliveryDate\", \"21/10/2014\");\n            json.put(\"time\", 9);\n            json.put(\"minute\", 0);\n\n            JSONArray SMSList = new JSONArray();\n            JSONObject sms1 = new JSONObject();\n            sms1.put(\"phoneNumber\", \"06xxxxxxx1\");\n            sms1.put(\"message\", \"msg 0\");\n            SMSList.put(sms1);\n\n            JSONObject sms2 = new JSONObject();\n            sms2.put(\"phoneNumber\", \"06xxxxxxx2\");\n            sms2.put(\"message\", \"msg 1\");\n            SMSList.put(sms2);\n\n            json.put(\"SMSList\", SMSList);\n\n        \n        // √âcriture des donn√©es JSON dans le corps de la requ√™te HTTP\n            OutputStream os = conn.getOutputStream(); \n            os.write(json.toString().getBytes());\n            os.flush();\n\n        // Lecture de la r√©ponse de l\'API\n            BufferedReader br = new BufferedReader(new InputStreamReader(conn.getInputStream()));\n            String response = br.lines().collect(Collectors.joining());\n            System.out.println(response);\n            \n        // Fermeture de la connexion HTTP\n            conn.disconnect();\n        } catch (Exception e) {\n            e.printStackTrace();\n        }\n    }\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"swift\" role=\"tabpanel\" aria-labelledby=\"swift-tab\">\n    <!-- SWIFT code example goes here -->\n    <pre><code class=\"language-swift\">\nimport SwiftUI\n\n// Cette requ√™te est utilis√©e pour envoyer des SMS par lot de 500 en temps r√©el ou en diff√©r√©\nstruct SMSParLotsView: View {\n    var body: some View {\n        // Un bouton qui appelle la fonction sendSMSParLots() lorsqu\'il est press√©\n        Button(action: {\n            sendSMSParLots()\n        }) {\n            Text(\"Send Bulk SMS\")\n                .font(.system(size: 20))\n                .foregroundColor(.white)\n                .frame(minWidth: 0, maxWidth: .infinity)\n                .padding()\n                .background(LinearGradient(gradient: Gradient(colors: [Color.blue, Color.blue.opacity(0.8)]), startPoint: .top, endPoint: .bottom))\n                .cornerRadius(10)\n                .padding(.horizontal)\n        }\n    }\n    \n\n    func sendSMSParLots() {\n        // Liste des SMS √† envoyer\n        let smsList: [[String: String]] = [\n            [\n                \"phoneNumber\": \"06XXXXXXXX\", // Num√©ro de t√©l√©phone du destinataire\n                \"message\": \"msg 0 en temps r√©el\" // Message √† envoyer\n            ],\n            [\n                \"phoneNumber\": \"06XXXXXXXX\", // Un autre num√©ro de t√©l√©phone\n                \"message\": \"msg 1 en temps r√©el\" // Un autre message\n            ]\n        ]\n\n        // Donn√©es √† envoyer √† l\'API\n        let data: [String: Any] = [\n            \"apiKey\": \"XXXXXXXXXXXX YOUR API KEY XXXXXXXXXXXXX\", // Votre cl√© API\n            \"sender\": \"mycompany\", // L\'exp√©diteur du SMS\n            // Ces 3 lignes permettent de programmer un envoi diff√©r√©.\n            //\"scheduledDeliveryDate\": \"12/05/2023\", // Date de livraison pr√©vue\n            //\"time\": 9, // Heure de livraison\n            //\"minute\": 0, // Minute de livraison\n            \"SMSList\": smsList // La liste des SMS √† envoyer\n        ]\n\n        // Conversion des donn√©es en JSON\n        guard let jsonData = try? JSONSerialization.data(withJSONObject: data, options: []) else {\n            fatalError(\"Error converting data to JSON.\")\n        }\n\n        // Cr√©ation de la requ√™te HTTP\n        let url = URL(string: \"https://api.smspartner.fr/v1/bulk-send\")! // URL de l\'API\n        var request = URLRequest(url: url)\n        request.httpMethod = \"POST\" // M√©thode HTTP\n        request.httpBody = jsonData // Corps de la requ√™te\n        request.addValue(\"application/json\", forHTTPHeaderField: \"Content-Type\") // Type de contenu\n        request.addValue(\"(jsonData.count)\", forHTTPHeaderField: \"Content-Length\") // Longueur du contenu\n        request.addValue(\"no-cache\", forHTTPHeaderField: \"cache-control\") // Contr√¥le du cache\n\n        // Envoi de la requ√™te HTTP\n        let task = URLSession.shared.dataTask(with: request) { (data, response, error) in\n            // V√©rification de la r√©ponse HTTP\n            guard let httpResponse = response as? HTTPURLResponse else {\n                fatalError(\"Error: Invalid HTTP response.\")\n            }\n            print(\"statusCode: (httpResponse.statusCode)\") // Impression du code d\'√©tat HTTP\n\n            // Gestion des erreurs √©ventuelles\n            if let error = error {\n                print(\"Error: (error.localizedDescription)\")\n            }\n\n            // Impression de la r√©ponse de l\'API\n            if let data = data {\n                if let responseString = String(data: data, encoding: .utf8) {\n                    print(\"Response: (responseString)\")\n                }\n            }\n        }\n        task.resume()\n    }\n}\n// Un aper√ßu de la vue\nstruct BulkSMSView_Previews: PreviewProvider {\n    static var previews: some View {\n        SMSParLotsView()\n    }\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"go\" role=\"tabpanel\" aria-labelledby=\"go-tab\">\n    <!-- GO code example goes here -->\n    <pre><code class=\"language-go\">\npackage main\n\nimport (\n	\"bytes\"\n	\"encoding/json\"\n	\"fmt\"\n	\"net/http\"\n)\n\nfunc main() {\n	apiKey := \"VOTRE CLE API\"\n	sender := \"monentreprise\"\n	scheduledDeliveryDate := \"21/10/2014\"\n	time := 9\n	minute := 0\n\n	// Construire les donn√©es JSON pour la requ√™te POST\n	data := map[string]interface{}{\n		\"apiKey\":               apiKey,\n		\"sender\":               sender,\n		\"scheduledDeliveryDate\": scheduledDeliveryDate,\n		\"time\":                 time,\n		\"minute\":               minute,\n		\"SMSList\": []map[string]string{\n			{\"phoneNumber\": \"06xxxxxxx1\", \"message\": \"msg 0\"},\n			{\"phoneNumber\": \"06xxxxxxx2\", \"message\": \"msg 1\"},\n		},\n	}\n\n	// Convertir les donn√©es en JSON\n	jsonData, err := json.Marshal(data)\n	if err != nil {\n		fmt.Println(\"Erreur lors de la conversion en JSON:\", err)\n		return\n	}\n\n	// Effectuer la requ√™te POST\n	url := \"https://api.smspartner.fr/v1/bulk-send\"\n	resp, err := http.Post(url, \"application/json\", bytes.NewBuffer(jsonData))\n	if err != nil {\n		fmt.Println(\"Erreur lors de l\'envoi de la requ√™te:\", err)\n		return\n	}\n	defer resp.Body.Close()\n\n	// Traiter la r√©ponse\n	if resp.StatusCode == http.StatusOK {\n		body := new(bytes.Buffer)\n		_, err := body.ReadFrom(resp.Body)\n		if err != nil {\n			fmt.Println(\"Erreur lors de la lecture de la r√©ponse:\", err)\n			return\n		}\n		fmt.Println(body.String())\n	} else {\n		fmt.Println(\"La requ√™te POST a √©chou√©. Code de r√©ponse:\", resp.StatusCode)\n	}\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"csharp\" role=\"tabpanel\" aria-labelledby=\"csharp-tab\">\n    <!-- C# code example goes here -->\n    <pre><code class=\"language-csharp\">\nusing System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\nusing Newtonsoft.Json;\n\nclass Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    static async Task Main(string[] args)\n    {\n        var request = new\n        {\n            apiKey = \"YOUR_API_KEY\",\n            sender = \"mycompany\",\n            scheduledDeliveryDate = \"21/10/2014\",\n            time = 9,\n            minute = 0,\n            SMSList = new[]\n            {\n                new\n                {\n                    phoneNumber = \"06xxxxxxx1\",\n                    message = \"msg 0\"\n                },\n                new\n                {\n                    phoneNumber = \"06xxxxxxx2\",\n                    message = \"msg 1\"\n                }\n            }\n        };\n\n        var content = new StringContent(\n            JsonConvert.SerializeObject(request),\n            Encoding.UTF8,\n            \"application/json\");\n\n        HttpResponseMessage response = await client.PostAsync(\"https://api.smspartner.fr/v1/bulk-send\", content);\n\n        if (response.IsSuccessStatusCode)\n        {\n            var result = await response.Content.ReadAsStringAsync();\n            Console.WriteLine(result);\n        }\n        else\n        {\n            Console.WriteLine(\"POST request failed with status code: \" + response.StatusCode);\n        }\n    }\n}\n   </code></pre>\n  </div>\n</div>\n\n## Responses\n\n### JSON\n```json\n{\n       \"success\": true,\n    \"code\": 200,\n    \"message_id\": xxxxxx,\n    \"currency\": \"EUR\",\n    \"cost\": 0.076,\n    \"nbSMS\": 2,\n    \"SMSResponse_List\": [\n        {\n            \"success\": true,\n            \"code\": 200,\n            \"nbSms\": 1,\n            \"cost\": 0.038,\n            \"phoneNumber\": \"+336xxxxxxx1\"\n        },\n        {\n            \"success\": true,\n            \"code\": 200,\n            \"nbSms\": 1,\n            \"cost\": 0.038,\n            \"phoneNumber\": \"+336xxxxxxx2\"\n        }\n    ]\n}\n```\n### XML\n```xml\n    <?xml version=\'1.0\' encoding=\'UTF-8\'?>\n<result>\n    <entry>true</entry>\n    <entry>200</entry>\n    <entry>xxxxxx</entry>\n    <entry>\n        <![CDATA[EUR]]>\n    </entry>\n    <entry>0.076</entry>\n    <entry>2</entry>\n    <entry>\n        <entry>\n            <entry>true</entry>\n            <entry>200</entry>\n            <entry>1</entry>\n            <entry>0.038</entry>\n            <entry>\n                <![CDATA[+336xxxxxxx1]]>\n            </entry>\n        </entry>\n        <entry>\n            <entry>true</entry>\n            <entry>200</entry>\n            <entry>1</entry>\n            <entry>0.038</entry>\n            <entry>\n                <![CDATA[+336xxxxxxx2]]>\n            </entry>\n        </entry>\n    </entry>\n</result>\n  ```\n', 'en', 18);
