equete) { (data, response, error) in\n            // Si une erreur survient, on l\'affiche\n            if let erreur = error {\n                print(\"Erreur : (erreur.localizedDescription)\")\n                return\n            }\n            \n            // Sinon, on affiche les donn√©es re√ßues\n            if let donnees = data {\n                if let resultat = String(data: donnees, encoding: .utf8) {\n                    print(resultat) // Impression du r√©sultat\n                }\n            }\n        }\n        \n        task.resume() // On lance la t√¢che\n    }\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"go\" role=\"tabpanel\" aria-labelledby=\"go-tab\">\n    <!-- GO code example goes here -->\n    <pre><code class=\"language-go\">\npackage main\n\nimport (\n	\"bytes\"\n	\"fmt\"\n	\"net/http\"\n)\n\nfunc main() {\n	apiKey := \"YOUR API KEY\"\n	to := \"336xxxxxxxx\"\n	from := \"336xxxxxxxx\"\n	message := \"This is your message\"\n\n	// Construire les donn√©es JSON pour la requ√™te POST\n	data := map[string]interface{}{\n		\"apiKey\":  apiKey,\n		\"to\":      to,\n		\"from\":    from,\n		\"message\": message,\n	}\n\n	// Convertir les donn√©es en JSON\n	jsonData, err := json.Marshal(data)\n	if err != nil {\n		fmt.Println(\"Erreur lors de la conversion en JSON:\", err)\n		return\n	}\n\n	// Effectuer la requ√™te POST\n	url := \"https://api.smspartner.fr/v1/vn/send\"\n	resp, err := http.Post(url, \"application/json\", bytes.NewBuffer(jsonData))\n	if err != nil {\n		fmt.Println(\"Erreur lors de l\'envoi de la requ√™te:\", err)\n		return\n	}\n	defer resp.Body.Close()\n\n	// Traiter la r√©ponse\n	if resp.StatusCode == http.StatusOK {\n		body := new(bytes.Buffer)\n		_, err := body.ReadFrom(resp.Body)\n		if err != nil {\n			fmt.Println(\"Erreur lors de la lecture de la r√©ponse:\", err)\n			return\n		}\n		fmt.Println(body.String())\n	} else {\n		fmt.Println(\"La requ√™te POST a √©chou√©. Code de r√©ponse:\", resp.StatusCode)\n	}\n}\n   </code></pre>\n  </div>\n  <div class=\"tab-pane fade\" id=\"csharp\" role=\"tabpanel\" aria-labelledby=\"csharp-tab\">\n    <!-- C# code example goes here -->\n    <pre><code class=\"language-csharp\">\nusing System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\nusing Newtonsoft.Json;\n\nclass Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    static async Task Main(string[] args)\n    {\n        var request = new\n        {\n            apiKey = \"YOUR_API_KEY\",\n            to = \"336xxxxxxxx\",\n            from = \"336xxxxxxxx\",\n            message = \"This is your message\"\n        };\n\n        var content = new StringContent(\n            JsonConvert.SerializeObject(request),\n            Encoding.UTF8,\n            \"application/json\");\n\n        HttpResponseMessage response = await client.PostAsync(\"https://api.smspartner.fr/v1/vn/send\", content);\n\n        if (response.IsSuccessStatusCode)\n        {\n            var result = await response.Content.ReadAsStringAsync();\n            Console.WriteLine(result);\n        }\n        else\n        {\n            Console.WriteLine(\"POST request failed with status code: \" + response.StatusCode);\n        }\n    }\n}\n   </code></pre>\n  </div>\n</div>\n\n## R√©ponses\n\n### JSON\n```json\n{\n   \"success\":true,\n   \"code\":200,\n   \"message_id\":xxx,\n   \"nb_sms\": 1,\n   \"cost\": xxx,\n   \"currency\": \"EUR\"\n}\n````\n<br> <br>\n', 'fr', 19),
