nel\" aria-labelledby=\"php-tab\">\r\n    <pre><code class=\"language-php\">\r\n&lt;? // Prepare data for GET request\r\n        $data = \'apiKey=YOUR_API_KEY&messageId=300&phoneNumber=06xxxxxxxx\';\r\n \r\n        $curl = curl_init();\r\n        curl_setopt($curl, CURLOPT_URL,\'https://api.smspartner.fr/v1/message-status?\'.$data);\r\n        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\r\n        curl_setopt($curl, CURLOPT_TIMEOUT, 10);\r\n \r\n \r\n        $result = curl_exec($curl);\r\n        curl_close($curl);\r\n \r\n        // Process your response here\r\n        echo $result;\r\n?&gt;\r\n    </code></pre>\r\n  </div>\r\n <div class=\"tab-pane fade\" id=\"vbnet\" role=\"tabpanel\" aria-labelledby=\"vbnet-tab\">\r\n   <pre><code class=\"language-vbnet\">\r\nImports System.IO\r\nImports System.Net\r\n \r\nModule Module1\r\n \r\n  Sub Main()\r\n \r\n    Dim base_url As String = \"http://api.smspartner.fr/v1/\"\r\n    Dim apiKey As String = \"VOTRE_APIKEY\"\r\n    Dim phoneNumber As String = \"06XXXXXXXX\"\r\n    Dim messageId As Integer = XXX\r\n \r\n    #check credits\r\n    Dim url As String\r\n    url = base_url & \"message-status\" & \"?apiKey=\" & apiKey & \"&phoneNumber=\" & phoneNumber & \"&messageId=\" & messageId\r\n \r\n    Dim credits As String\r\n    credits = apiRequest(\"GET\", url, Nothing)\r\n \r\n  End Sub\r\n \r\n  Function apiRequest(method As String, url As String, parameters As String) As String\r\n \r\n    Dim request As HttpWebRequest\r\n    request = WebRequest.Create(url)\r\n    request.Method = method\r\n    request.Timeout = 10000   # timeout in ms\r\n    request.ContentType = \"application/json; charset=utf-8\"\r\n    request.ContentLength = 0\r\n \r\n    #set POST data\r\n    If Not String.IsNullOrEmpty(parameters) Then\r\n      request.ContentLength = parameters.Length\r\n      Using reqStream As StreamWriter = New StreamWriter(request.GetRequestStream())\r\n        reqStream.Write(parameters)\r\n      End Using\r\n    End If\r\n \r\n    #get response\r\n    Dim returnValue As String = Nothing\r\n    Using response As HttpWebResponse = request.GetResponse()\r\n      If response.StatusCode = HttpStatusCode.OK Then\r\n        Using resStream = response.GetResponseStream()\r\n          If resStream IsNot Nothing Then\r\n            Using reader As New StreamReader(resStream)\r\n              returnValue = reader.ReadToEnd()\r\n            End Using\r\n          End If\r\n        End Using\r\n      End If\r\n    End Using\r\n    apiRequest = returnValue\r\n \r\n  End Function\r\n \r\nEnd Module\r\n\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"python\" role=\"tabpanel\" aria-labelledby=\"python-tab\">\r\n    <!-- Python code example goes here -->\r\n    <pre><code class=\"language-python\">\r\n# std\r\nimport logging\r\nimport json\r\nfrom collections import OrderedDict\r\n \r\n# 3p\r\nimport requests\r\n \r\nAPI_KEY = \"MY API KEY\"\r\nURL = \"https://api.smspartner.fr/v1\"\r\n \r\nclass SMSPartner():\r\n    def get_delivery(self,phone_numbers,message_id):\r\n		url = URL + \"/message-status?apiKey=\" + API_KEY + \"&phoneNumber=\" + phone_numbers + \"&messageId=\" + message_id\r\n		r = requests.get(url)\r\n		r_json = r.json()\r\n		if r_json.get(\"success\") == True:\r\n			print(r_json)\r\n			status = True\r\n		else:\r\n			print(r_json)\r\n			status = False\r\n		return status\r\n  </div>\r\n    <div class=\"tab-pane fade\" id=\"curl\" role=\"tabpanel\" aria-labelledby=\"curl-tab\">\r\n    <!-- cURL code example goes here -->\r\n    <pre><code class=\"language-bash\">\r\ncurl -H  \"Content-Type: application/json\" -X GET  https://api.smspartner.fr/v1/message-status?apiKey=xxx&messageId=300&phoneNumber=06xxxxxxxx\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"nodejs\" role=\"tabpanel\" aria-labelledby=\"nodejs-tab\">\r\n    <!-- NodeJS code example goes here -->\r\n    <pre><code class=\"language-javascript\">\r\nconst https = require(\"https\");\r\n\r\n// Remplacez par votre cl√© API et autres donn√©es\r\nconst apiKey = \"VOTRE_CL√â_API\";\r\nconst messageId = \"300\";\r\nconst phoneNumber = \"06xxxxxxxx\";\r\n\r\n// Pr√©parer les donn√©es pour la requ√™te GET\r\nconst data = `apiKey=${apiKey}&messageId=${messageId}&phoneNumber=${phoneNumber}`;\r\n\r\n// D√©finir les options pour la requ√™te HTTP GET vers l\'API SMS Partner\r\nconst options = {\r\n  hostname: \"api.smspartner.fr\",\r\n  port: 443,\r\n  path: `/v1/message-status?${data}`,\r\n  method: \"GET\",\r\n  headers: {\r\n    \"Content-Type\": \"application/json\",\r\n    \"cache-control\": \"no-cache\",\r\n  },\r\n};\r\n\r\n// Effectuer la requ√™te HTTP GET avec les options et donn√©es d√©finies pr√©c√©demment\r\nconst req = https.request(options, (res) => {\r\n  console.log(`statusCode: ${res.statusCode}`);\r\n\r\n  // Afficher les donn√©es de r√©ponse de l\'API sur la sortie standard\r\n  res.on(\"data\", (d) => {\r\n    process.stdout.write(d);\r\n  });\r\n});\r\n\r\n// Afficher en cas d\'erreur lors de l\'ex√©cution de la requ√™te HTTP GET\r\nreq.on(\"error\", (error) => {\r\n  console.error(error);\r\n});\r\n\r\n// Terminer la requ√™te HTTP GET\r\nreq.end();\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"java\" role=\"tabpanel\" aria-labelledby=\"java-tab\">\r\n    <!-- JAVA code example goes here -->\r\n   <pre><code class=\"language-java\">\r\nimport java.net.HttpURLConnection;\r\nimport java.net.URL;\r\nimport java.io.BufferedReader;\r\nimport java.io.InputStreamReader;\r\nimport java.util.stream.Collectors;\r\nimport org.json.JSONObject;\r\n\r\npublic class SMSSTatus {\r\n    public static void main(String[] args) {\r\n        try {\r\n            // Remplacez par votre cl√© API et autres donn√©es\r\n            String apiKey = \"VOTRE_CL√â_API\";\r\n            String messageId = \"300\";\r\n            String phoneNumber = \"06xxxxxxxx\";\r\n\r\n            // Pr√©parer les donn√©es pour la requ√™te GET\r\n            String data = String.format(\"apiKey=%s&messageId=%s&phoneNumber=%s\", apiKey, messageId, phoneNumber);\r\n            URL url = new URL(\"https://api.smspartner.fr/v1/message-status?\" + data);\r\n            HttpURLConnection conn = (HttpURLConnection) url.openConnection();\r\n            conn.setRequestMethod(\"GET\");\r\n            conn.setRequestProperty(\"Content-Type\", \"application/json\");\r\n            conn.setRequestProperty(\"cache-control\", \"no-cache\");\r\n\r\n            // Lecture de la r√©ponse de l\'API\r\n            BufferedReader br = new BufferedReader(new InputStreamReader(conn.getInputStream()));\r\n            String response = br.lines().collect(Collectors.joining());\r\n\r\n            // Afficher la r√©ponse JSON\r\n            System.out.println(response);\r\n\r\n            // Fermeture de la connexion HTTP\r\n            conn.disconnect();\r\n        } catch (Exception e) {\r\n            e.printStackTrace();\r\n        }\r\n    }\r\n}\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"swift\" role=\"tabpanel\" aria-labelledby=\"swift-tab\">\r\n    <!-- SWIFT code example goes here -->\r\n    <pre><code class=\"language-swift\">\r\nimport SwiftUI\r\n\r\n// Structure pour la vue \"StatutSMS\"\r\nstruct StatutSMS: View {\r\n   var body: some View {\r\n       // Un bouton qui appelle la fonction StatutSMS() lorsqu\'il est press√©\r\n       Button(action: {\r\n           StatutSMS()\r\n       }) {\r\n           Text(\"Check SMS Status\")\r\n               .font(.system(size: 20))\r\n               .foregroundColor(.white)\r\n               .frame(minWidth: 0, maxWidth: .infinity)\r\n               .padding()\r\n               .background(LinearGradient(gradient: Gradient(colors: [Color.blue, Color.blue.opacity(0.8)]), startPoint: .top, endPoint: .bottom))\r\n               .cornerRadius(10)\r\n               .padding(.horizontal)\r\n       }\r\n   }\r\n   \r\n   // Fonction pour v√©rifier le statut d\'un SMS\r\n   func StatutSMS() {\r\n       let apiKey = \"XXXXXXXXXXXX YOUR API KEY XXXXXXXXXXXXX\" // Votre cl√© API\r\n       let messageId = \"your msg ID\" // ID de votre message\r\n       let phoneNumber = \"06XXXXXXXX\" // Num√©ro de t√©l√©phone\r\n\r\n       // URL pour v√©rifier le statut du SMS\r\n       let urlString = \"https://api.smspartner.fr/v1/message-status?apiKey=(apiKey)&messageId=(messageId)&phoneNumber=(phoneNumber)\"\r\n\r\n       // On v√©rifie que l\'URL est correctement form√©e\r\n       guard let url = URL(string: urlString) else {\r\n           print(\"Erreur : impossible de cr√©er l\'URL\")\r\n           return\r\n       }\r\n\r\n       // T√¢che pour r√©cup√©rer les donn√©es de l\'URL\r\n       let task = URLSession.shared.dataTask(with: url) { (data, response, error) in\r\n           // Si une erreur se produit, on l\'affiche\r\n           if let error = error {\r\n               print(\"Erreur : (error)\")\r\n           }\r\n           // Sinon, on affiche les donn√©es re√ßues\r\n           else if let data = data {\r\n               let str = String(data: data, encoding: .utf8)\r\n               print(\"Donn√©es re√ßues :\n(str ?? \"\")\")\r\n           }\r\n       }\r\n       \r\n       task.resume() // On lance la t√¢che\r\n   }\r\n}\r\n\r\n// Aper√ßu de la vue\r\nstruct StatutSMS_Previews: PreviewProvider {\r\n   static var previews: some View {\r\n       StatutSMS()\r\n   }\r\n}\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"go\" role=\"tabpanel\" aria-labelledby=\"go-tab\">\r\n    <!-- GO code example goes here -->\r\n    <pre><code class=\"language-go\">\r\npackage main\r\n\r\nimport (\r\n	\"io/ioutil\"\r\n	\"log\"\r\n	\"net/http\"\r\n	\"time\"\r\n)\r\n\r\nfunc main() {\r\n	// Prepare data for GET request\r\n	apiKey := \"YOUR_API_KEY\"\r\n	messageId := \"300\"\r\n	phoneNumber := \"06xxxxxxxx\"\r\n\r\n	// Create GET request URL\r\n	url := \"https://api.smspartner.fr/v1/message-status?\" +\r\n		\"apiKey=\" + apiKey + \"&messageId=\" + messageId + \"&phoneNumber=\" + phoneNumber\r\n\r\n	// Create HTTP client\r\n	client := &http.Client{Timeout: 10 * time.Second}\r\n\r\n	// Send GET request\r\n	resp, err := client.Get(url)\r\n	if err != nil {\r\n		log.Fatalf(\"Error sending request: %v\", err)\r\n	}\r\n	defer resp.Body.Close()\r\n\r\n	// Get response\r\n	body, err := ioutil.ReadAll(resp.Body)\r\n	if err != nil {\r\n		log.Fatalf(\"Error reading response body: %v\", err)\r\n	}\r\n\r\n	// Process your response here\r\n	log.Printf(\"Response: %s\", body)\r\n}\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"csharp\" role=\"tabpanel\" aria-labelledby=\"csharp-tab\">\r\n    <!-- C# code example goes here -->\r\n    <pre><code class=\"language-csharp\">\r\nusing System;\r\nusing System.Net.Http;\r\nusing System.Threading.Tasks;\r\n\r\nclass Program\r\n{\r\n    private static readonly HttpClient client = new HttpClient();\r\n\r\n    static async Task Main(string[] args)\r\n    {\r\n        var apiKey = \"YOUR_API_KEY\";\r\n        var messageId = \"300\";\r\n        var phoneNumber = \"06xxxxxxxx\";\r\n        var uri = new Uri($\"https://api.smspartner.fr/v1/message-status?apiKey={apiKey}&messageId={messageId}&phoneNumber={phoneNumber}\");\r\n\r\n        HttpResponseMessage response = await client.GetAsync(uri);\r\n\r\n        if (response.IsSuccessStatusCode)\r\n        {\r\n            var result = await response.Content.ReadAsStringAsync();\r\n            Console.WriteLine(result);\r\n        }\r\n        else\r\n        {\r\n            Console.WriteLine(\"GET request failed with status code: \" + response.StatusCode);\r\n        }\r\n    }\r\n}\r\n   </code></pre>\r\n  </div>\r\n</div>\r\n\r\n## R√©ponses\r\n\r\n### JSON\r\n```json\r\n{\r\n    \"success\": true,\r\n    \"code\": 200,\r\n    \"number\": \"0600000000\",\r\n    \"messageId\": \"1111\",\r\n    \"stopSms\": false,\r\n    \"date\": \"2015-02-07 22:39:46\",\r\n    \"statut\": \"Delivered\",\r\n    \"cost\": 0.045,\r\n    \"countryCode\": \"FR\",\r\n    \"currency\": \"EUR\"\r\n}\r\n```\r\n### 3 statuts de remises possibles\r\n* **Delivered (Livr√©)** : Ce statut indique que le message a √©t√© livr√© avec succ√®s au destinataire. Cela signifie que le message a √©t√© envoy√© correctement et qu‚Äôil a √©t√© re√ßu sur l‚Äôappareil ou la plateforme du destinataire. La date et l‚Äôheure de la r√©ception est normalement disponible.\r\n* **Not delivered (Non livr√©)** : Ce statut fait r√©f√©rence aux messages qui n‚Äôont pas pu √™tre livr√©s au destinataire. Il peut y avoir plusieurs raisons pour lesquelles un message n‚Äôest pas livr√© : comme un num√©ro de t√©l√©phone non valide ou un probl√®me op√©rateurs chez un op√©rateur.\r\n* **Waiting (En attente)** : Ce statut indique que le message est en cours de livraison et qu‚Äôil n‚Äôa pas encore √©t√© confirm√© s‚Äôil sera livr√© ou non. Cela peut se produire en cas de retards sur le r√©seau ou lorsque des confirmations suppl√©mentaires sont n√©cessaires avant de pouvoir marquer le message comme livr√© ou non livr√©.\r\n', 'fr', 25),
