\n                }\r\n            }\r\n        }\r\n\r\n        task.resume()\r\n    }\r\n}\r\n\r\nstruct StatutSmsParID_Previews: PreviewProvider {\r\n    static var previews: some View {\r\n        StatutSmsParID()\r\n    }\r\n}\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"go\" role=\"tabpanel\" aria-labelledby=\"go-tab\">\r\n    <!-- GO code example goes here -->\r\n    <pre><code class=\"language-go\">\r\npackage main\r\n\r\nimport (\r\n	\"io/ioutil\"\r\n	\"log\"\r\n	\"net/http\"\r\n	\"time\"\r\n)\r\n\r\nfunc main() {\r\n	// Prepare data for GET request\r\n	apiKey := \"YOUR_API_KEY\"\r\n	messageId := \"300\"\r\n\r\n	// Create GET request URL\r\n	url := \"https://api.smspartner.fr/v1/bulk-status?\" +\r\n		\"apiKey=\" + apiKey + \"&messageId=\" + messageId\r\n\r\n	// Create HTTP client\r\n	client := &http.Client{Timeout: 10 * time.Second}\r\n\r\n	// Send GET request\r\n	resp, err := client.Get(url)\r\n	if err != nil {\r\n		log.Fatalf(\"Error sending request: %v\", err)\r\n	}\r\n	defer resp.Body.Close()\r\n\r\n	// Get response\r\n	body, err := ioutil.ReadAll(resp.Body)\r\n	if err != nil {\r\n		log.Fatalf(\"Error reading response body: %v\", err)\r\n	}\r\n\r\n	// Process your response here\r\n	log.Printf(\"Response: %s\", body)\r\n}\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"csharp\" role=\"tabpanel\" aria-labelledby=\"csharp-tab\">\r\n    <!-- C# code example goes here -->\r\n    <pre><code class=\"language-csharp\">\r\nusing System;\r\nusing System.Net.Http;\r\nusing System.Threading.Tasks;\r\n\r\nclass Program\r\n{\r\n    private static readonly HttpClient client = new HttpClient();\r\n\r\n    static async Task Main(string[] args)\r\n    {\r\n        var apiKey = \"YOUR_API_KEY\";\r\n        var messageId = \"300\";\r\n        var uri = new Uri($\"https://api.smspartner.fr/v1/bulk-status?apiKey={apiKey}&messageId={messageId}\");\r\n\r\n        HttpResponseMessage response = await client.GetAsync(uri);\r\n\r\n        if (response.IsSuccessStatusCode)\r\n        {\r\n            var result = await response.Content.ReadAsStringAsync();\r\n            Console.WriteLine(result);\r\n        }\r\n        else\r\n        {\r\n            Console.WriteLine(\"GET request failed with status code: \" + response.StatusCode);\r\n        }\r\n    }\r\n}\r\n   </code></pre>\r\n  </div>\r\n</div>\r\n\r\n## R√©ponses\r\n\r\n### JSON\r\n```json\r\n{\r\n    \"success\": true,\r\n    \"code\": 200,\r\n    \"message_id\": \"111\",\r\n    \"StatutResponse_List\": [\r\n        {\r\n            \"phoneNumber\": \"+336XXXXXXX1\",\r\n            \"status\": \"Delivered\",\r\n            \"stopSMS\": false,\r\n            \"date\": \"1517989111\"\r\n        },\r\n        {\r\n            \"phoneNumber\": \"+336XXXXXXX2\",\r\n            \"status\": \"Delivered\",\r\n            \"stopSMS\": false,\r\n            \"date\": \"1517989101\"\r\n        }\r\n             ...\r\n}\r\n```\r\n### 3 statuts de remises possibles\r\n* **Delivered**\r\n* **Not delivered**\r\n* **Waiting**\r\n', 'fr', 29);
