labelledby=\"php-tab\">\r\n    <pre><code class=\"language-php\">\r\n&lt;? \r\n        // Prepare data for GET request\r\n        $data = \'apiKey=YOUR_API_KEY\';\r\n \r\n        $curl = curl_init();\r\n        curl_setopt($curl, CURLOPT_URL,\'https://api.smspartner.fr/v1/subaccount/list?\'.$data);\r\n        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\r\n        curl_setopt($curl, CURLOPT_TIMEOUT, 10);\r\n \r\n \r\n        $result = curl_exec($curl);\r\n        curl_close($curl);\r\n \r\n        // Process your response here\r\n        echo $result;\r\n\r\n?&gt;\r\n    </code></pre>\r\n  </div>\r\n <div class=\"tab-pane fade\" id=\"vbnet\" role=\"tabpanel\" aria-labelledby=\"vbnet-tab\">\r\n   <pre><code class=\"language-vbnet\">\r\nImports System.IO\r\nImports System.Net\r\n \r\nModule Module1\r\n \r\n  Sub Main()\r\n \r\n    Dim base_url As String = \"http://api.smspartner.fr/v1/\"\r\n    Dim apiKey As String = \"VOTRE_APIKEY\"\r\n \r\n    #check credits\r\n    Dim url As String\r\n    url = base_url & \"subaccount/list\" & \"?apiKey=\" & apiKey\r\n \r\n    Dim credits As String\r\n    credits = apiRequest(\"GET\", url, Nothing)\r\n \r\n  End Sub\r\n \r\n  Function apiRequest(method As String, url As String, parameters As String) As String\r\n \r\n    Dim request As HttpWebRequest\r\n    request = WebRequest.Create(url)\r\n    request.Method = method\r\n    request.Timeout = 10000   # timeout in ms\r\n    request.ContentType = \"application/json; charset=utf-8\"\r\n    request.ContentLength = 0\r\n \r\n    #set POST data\r\n    If Not String.IsNullOrEmpty(parameters) Then\r\n      request.ContentLength = parameters.Length\r\n      Using reqStream As StreamWriter = New StreamWriter(request.GetRequestStream())\r\n        reqStream.Write(parameters)\r\n      End Using\r\n    End If\r\n \r\n    #get response\r\n    Dim returnValue As String = Nothing\r\n    Using response As HttpWebResponse = request.GetResponse()\r\n      If response.StatusCode = HttpStatusCode.OK Then\r\n        Using resStream = response.GetResponseStream()\r\n          If resStream IsNot Nothing Then\r\n            Using reader As New StreamReader(resStream)\r\n              returnValue = reader.ReadToEnd()\r\n            End Using\r\n          End If\r\n        End Using\r\n      End If\r\n    End Using\r\n    apiRequest = returnValue\r\n \r\n  End Function\r\n \r\nEnd Module\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"python\" role=\"tabpanel\" aria-labelledby=\"python-tab\">\r\n <pre><code class=\"language-python\">\r\n# std\r\nimport logging\r\nimport json\r\nfrom collections import OrderedDict\r\n \r\n# 3p\r\nimport requests\r\n \r\nAPI_KEY = \"MY API KEY\"\r\nURL = \"https://api.smspartner.fr/v1\"\r\n \r\nclass SMSPartner():\r\n    def get_list_subaccount(self):\r\n		url = URL + \"/subaccount/list?apiKey=\" + API_KEY\r\n		r = requests.get(url)\r\n		r_json = r.json()\r\n		if r_json.get(\"success\") == True:\r\n			print(r_json)\r\n			status = True\r\n		else:\r\n			print(r_json)\r\n			status = False\r\n		return status\r\n   </code></pre>\r\n  </div>\r\n    <div class=\"tab-pane fade\" id=\"curl\" role=\"tabpanel\" aria-labelledby=\"curl-tab\">\r\n    <!-- cURL code example goes here -->\r\n    <pre><code class=\"language-bash\">\r\ncurl -H  \"Content-Type: application/json\" -X\r\n GET  https://api.smspartner.fr/v1/subaccount/list?apiKey=xxx&page=1\r\n  </code></pre>\r\n  </div>\r\n\r\n    <div class=\"tab-pane fade\" id=\"nodejs\" role=\"tabpanel\" aria-labelledby=\"nodejs-tab\">\r\n    <!-- NodeJS code example goes here -->\r\n    <pre><code class=\"language-javascript\">\r\n\r\nconst https = require(\'https\');\r\n\r\n// Permets de r√©cup√©rer la liste des sous-comptes\r\nlet data = \'apiKey=YOUR_API_KEY\';\r\nlet url = \'https://api.smspartner.fr/v1/subaccount/list?\' + data;\r\n\r\nhttps.get(url, (res) => {\r\n  let data = \'\';\r\n\r\n  res.on(\'data\', (chunk) => {\r\n    data += chunk;\r\n  });\r\n\r\n  res.on(\'end\', () => {\r\n    console.log(JSON.parse(data));\r\n  });\r\n\r\n}).on(\"error\", (err) => {\r\n  console.log(\"Erreur: \" + err.message);\r\n});\r\n        </code></pre>\r\n  </div>\r\n\r\n  <div class=\"tab-pane fade\" id=\"java\" role=\"tabpanel\" aria-labelledby=\"java-tab\">\r\n    <!-- JAVA code example goes here -->\r\n   <pre><code class=\"language-java\">\r\nimport java.io.BufferedReader;\r\nimport java.io.InputStreamReader;\r\nimport java.net.HttpURLConnection;\r\nimport java.net.URL;\r\n\r\npublic class R√©cup√©rationDesSousComptes {\r\n    public static void main(String[] args) {\r\n        try {\r\n            // Prepare data for GET request\r\n            String apiKey = \"YOUR_API_KEY\";\r\n\r\n            // Create GET request URL\r\n            String urlString = \"https://api.smspartner.fr/v1/subaccount/list?\" +\r\n                    \"apiKey=\" + apiKey;\r\n\r\n            // Create URL object\r\n            URL url = new URL(urlString);\r\n\r\n            // Create HTTP connection\r\n            HttpURLConnection connection = (HttpURLConnection) url.openConnection();\r\n            connection.setRequestMethod(\"GET\");\r\n            connection.setConnectTimeout(10000);\r\n            connection.setReadTimeout(10000);\r\n\r\n            // Send GET request\r\n            int responseCode = connection.getResponseCode();\r\n\r\n            // Get response\r\n            BufferedReader reader;\r\n            if (responseCode >= 200 && responseCode <= 299) {\r\n                reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));\r\n            } else {\r\n                reader = new BufferedReader(new InputStreamReader(connection.getErrorStream()));\r\n            }\r\n\r\n            StringBuilder response = new StringBuilder();\r\n            String line;\r\n            while ((line = reader.readLine()) != null) {\r\n                response.append(line);\r\n            }\r\n            reader.close();\r\n\r\n            // Process your response here\r\n            System.out.println(response.toString());\r\n        } catch (Exception e) {\r\n            e.printStackTrace();\r\n        }\r\n    }\r\n}\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"swift\" role=\"tabpanel\" aria-labelledby=\"swift-tab\">\r\n    <!-- SWIFT code example goes here -->\r\n    <pre><code class=\"language-swift\">\r\n import SwiftUI\r\n\r\nstruct ListeDesSousComptes: View {\r\n    @State private var result: String = \"Loading...\"\r\n\r\n    var body: some View {\r\n        VStack {\r\n            Text(\"Liste Des Sous Comptes\")\r\n                .font(.title)\r\n                .padding()\r\n\r\n            Text(result)\r\n                .font(.system(size: 20))\r\n                .padding()\r\n        }\r\n        .onAppear(perform: loadSubAccounts)\r\n    }\r\n\r\n    func loadSubAccounts() {\r\n        let apiKey = \"YOUR_API_KEY\"\r\n        let urlString = \"https://api.smspartner.fr/v1/subaccount/list?apiKey=(apiKey)\"\r\n\r\n        guard let url = URL(string: urlString) else {\r\n            print(\"Invalid URL\")\r\n            return\r\n        }\r\n\r\n        let task = URLSession.shared.dataTask(with: url) { (data, response, error) in\r\n            if let error = error {\r\n                print(\"Error: (error)\")\r\n            } else if let data = data {\r\n                let resultString = String(data: data, encoding: .utf8)\r\n                DispatchQueue.main.async {\r\n                    self.result = resultString ?? \"Error\"\r\n                }\r\n            }\r\n        }\r\n\r\n        task.resume()\r\n    }\r\n}\r\n\r\nstruct ListeDesSousComptes_Previews: PreviewProvider {\r\n    static var previews: some View {\r\n        ListeDesSousComptes()\r\n    }\r\n}\r\n   </code></pre>\r\n  </div>\r\n  <div class=\"tab-pane fade\" id=\"go\" role=\"tabpanel\" aria-labelledby=\"go-tab\">\r\n<pre><code class=\"language-go\">\r\npackage main\r\n\r\nimport (\r\n	\"io/ioutil\"\r\n	\"log\"\r\n	\"net/http\"\r\n)\r\n\r\nfunc main() {\r\n	apiKey := \"YOUR_API_KEY\"\r\n	url := \"https://api.smspartner.fr/v1/subaccount/list?apiKey=\" + apiKey\r\n\r\n	// Create GET request\r\n	resp, err := http.Get(url)\r\n	if err != nil {\r\n		log.Fatalf(\"Error sending request: %v\", err)\r\n	}\r\n	defer resp.Body.Close()\r\n\r\n	// Read the response\r\n	body, err := ioutil.ReadAll(resp.Body)\r\n	if err != nil {\r\n		log.Fatalf(\"Error reading response: %v\", err)\r\n	}\r\n\r\n	// Print the response body\r\n	log.Printf(\"Response body: %s\", string(body))\r\n}\r\n   </code></pre>\r\n  </div>\r\n\r\n  <div class=\"tab-pane fade\" id=\"csharp\" role=\"tabpanel\" aria-labelledby=\"csharp-tab\">\r\n    <!-- C# code example goes here -->\r\n    <pre><code class=\"language-csharp\">\r\nusing System;\r\nusing System.Net.Http;\r\nusing System.Threading.Tasks;\r\n\r\nclass Program\r\n{\r\n    private static readonly HttpClient client = new HttpClient();\r\n\r\n    static async Task Main(string[] args)\r\n    {\r\n        string apiKey = \"YOUR_API_KEY\";\r\n        var uri = new Uri($\"https://api.smspartner.fr/v1/subaccount/list?apiKey={apiKey}\");\r\n\r\n        HttpResponseMessage response = await client.GetAsync(uri);\r\n\r\n        if (response.IsSuccessStatusCode)\r\n        {\r\n            string result = await response.Content.ReadAsStringAsync();\r\n            Console.WriteLine(result);\r\n        }\r\n        else\r\n        {\r\n            Console.WriteLine(\"GET request failed with status code: \" + response.StatusCode);\r\n        }\r\n    }\r\n}\r\n   </code></pre>\r\n  </div>\r\n</div>\r\n\r\n## Response\r\n\r\n### JSON\r\n```json\r\n{\r\n  \"success\": true,\r\n  \"code\": 200,\r\n  \"total\": 29,\r\n  \"nb_per_page\": 20,\r\n  \"page\": 1,\r\n  \"data\": [\r\n    {\r\n      \"id\": 1,\r\n      \"email\": \"email1\",\r\n      \"type\": \"simple\",\r\n      \"token\": \"token1\", //Est utilis√© comme identifiant du sous-compte pour ajouter des cr√©dits\r\n      \"apiKey\": \"ApiKey sous compte\",\r\n      \"createdAt\": \"2016-03-04 11:23:14\",\r\n      \"credits\": {\r\n          \"balance\": \"0.800\",\r\n          \"currency\": \"EUR\"\r\n      }\r\n    },\r\n    {\r\n      \"id\": 2,\r\n      \"email\": \"email2\",\r\n      \"type\": \"simple\",\r\n      \"token\": \"token2\",\r\n      \"apiKey\": \"ApiKey2 du sous compte\",\r\n      \"createdAt\": \"2016-03-04 11:23:14\",\r\n      \"credits\": {\r\n          \"balance\": \"10.245\",\r\n          \"currency\": \"EUR\"\r\n      }\r\n    }\r\n  ]\r\n}\r\n```\r\n', 'en', 69),
(62, '## Requ√™te\nExemple de requ√™te :\n\n<!-- Nav tabs -->\n<ul class=\"nav nav-tabs\" id=\"myTab\" role=\"tablist\">\n  <li class=\"nav-item\">\n    <a class=\"nav-link active\" id=\"php-tab\" data-toggle=\"tab\" href=\"#php\" role=\"tab\" aria-controls=\"php\" aria-selected=\"true\">PHP</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"vbnet-tab\" data-toggle=\"tab\" href=\"#vbnet\" role=\"tab\" aria-controls=\"vbnet\" aria-selected=\"false\">VB.net</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"python-tab\" data-toggle=\"tab\" href=\"#python\" role=\"tab\" aria-controls=\"python\" aria-selected=\"false\">Python</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"curl-tab\" data-toggle=\"tab\" href=\"#curl\" role=\"tab\" aria-controls=\"curl\" aria-selected=\"false\">cURL</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"nodejs-tab\" data-toggle=\"tab\" href=\"#nodejs\" role=\"tab\" aria-controls=\"nodejs\" aria-selected=\"false\">NodeJS</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"java-tab\" data-toggle=\"tab\" href=\"#java\" role=\"tab\" aria-controls=\"java\" aria-selected=\"false\">JAVA</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"swift-tab\" data-toggle=\"tab\" href=\"#swift\" role=\"tab\" aria-controls=\"swift\" aria-selected=\"false\">SWIFT</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"go-tab\" data-toggle=\"tab\" href=\"#go\" role=\"tab\" aria-controls=\"go\" aria-selected=\"false\">GO</a>\n  </li>\n  <li class=\"nav-item\">\n    <a class=\"nav-link\" id=\"csharp-tab\" data-toggle=\"tab\" href=\"#csharp\" role=\"tab\" aria-controls=\"csharp\" aria-selected=\"false\">C#</a>\n  </li>\n</ul>\n\n\n<!-- Tab panes -->\n<div class=\"tab-content\">\n  <div class=\"tab-pane fade show active\" id=\"php\" role=\"tabpanel\" aria-labelledby=\"php-tab\">\n    <pre><code class=\"language-php\">\n&lt;? \n        // Prepare data for POST request\n        $fields = array(\n            \'apiKey\'=> \'YOUR API KEY\',\n            \'credit\'=> \'100\',\n            \'tokenSubaccount\'=>\'identifiant du sous-compte\'\n            );\n \n \n        $curl = curl_init();\n        curl_setopt($curl, CURLOPT_URL,\'https://api.smspartner.fr/v1/subaccount/credit/add\');\n        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\n        curl_setopt($curl, CURLOPT_TIMEOUT, 10);\n        curl_setopt($curl, CURLOPT_POST, 1);\n        curl_setopt($curl, CURLOPT_POSTFIELDS,json_encode($fields));\n \n        $result = curl_exec($curl);\n        curl_close($curl);\n \n        // Process your response here\n        echo $result;\n?&gt;\n    </code></pre>\n  </div>\n <div class=\"tab-pane fade\" id=\"vbnet\" role=\"tabpanel\" aria-labelledby=\"vbnet-tab\">\n   <pre><code class=\"language-vbnet\">\nImports System.IO\nImports System.Net\n \nModule Module1\n \n  Sub Main()\n \n    Dim base_url As String = \"http://api.smspartner.fr/v1/\"\n    Dim apiKey As String = \"VOTRE_APIKEY\"\n \n    #send sms\n    url = base_url & \"subaccount/credit/sms\"\n    #note : utiliser une librairie JSON en production, par exemple :\n    #https//www.nuget.org/packages/Newtonsoft.Json/\n    Dim parameters As String = String.Format(\n        \"{{\"\"apiKey\"\":\"\"{0}\"\",\"\"credit\"\":\"\"{1}\"\",\"\"tokenSubaccount\"\":\"\"{2}\"\"}}\",\n        apiKey,\n        50,\n        \"identifiant du sous-compte\")\n    Console.Write(parameters)\n    apiRequest(\"POST\", url, parameters)\n \n  End Sub\n \n  Function apiRequest(method As String, url As String, parameters As String) As String\n \n    Dim request As HttpWebRequest\n    request = WebRequest.Create(url)\n    request.Method = method\n    request.Timeout = 10000   # timeout in ms\n    request.ContentType = \"application/json; charset=utf-8\"\n    request.ContentLength = 0\n \n    #set POST data\n    If Not String.IsNullOrEmpty(parameters) Then\n      requ